def HammingDistance(p, q):
    #Calculates the number of mismatches between two strings
    return sum(1 for a, b in zip(p, q) if a != b)

def DistanceBetweenPatternAndStrings(Pattern, Dna):
    #calculate the sum of min hamming distance between strand and a dna seq
    k = len(Pattern)
    distance = 0
    
    for text in Dna:
        min_hamming_dist = float('inf')
        
        for i in range(len(text) - k + 1):
            pattern_prime = text[i:i+k]
            
            current_dist = HammingDistance(Pattern, pattern_prime)
            if min_hamming_dist > current_dist:
                min_hamming_dist = current_dist
        
        distance += min_hamming_dist
        
    return distance

